import { useState, useEffect } from "react";

const Header = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 190) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);
  useEffect(() => {
    // Add or remove the class from the body based on the "scrolled" state
    if (scrolled) {
      document.body.classList.add("scrolled");
    } else {
      document.body.classList.remove("scrolled");
    }

    // Cleanup the class when the component unmounts
    return () => {
      document.body.classList.remove("scrolled");
    };
  }, [scrolled]);
  return (
    <div className={`navbar fixed-top ${scrolled ? "sticky" : ""}`}>
      <div className="container-fluid nav-cont">
        <div className="nav-child1">
          <a className="navbar-brand" href="/#">
            <img className="logo" src="nova2.svg" alt="" />
            <img className="logo" src="tire.svg" alt="" />
          </a>
        </div>

        <div class="navbar-collapse nav-child2" id="navbarNav">
          <ul class="navbar-nav">
            <li class="nav-item">
              <a class="nav-link active" href="#">
                Home
              </a>
            </li>
            <li class="nav-item">
              <a class="nav-link" href="#">
                Vehicles
              </a>
            </li>
            <li class="nav-item">
              <a class="nav-link" href="#">
                About
              </a>
            </li>
            <li class="nav-item">
              <a class="nav-link">
                Testimonials
              </a>
            </li>
            <li class="nav-item">
              <a class="nav-link">
                Contact
              </a>
            </li>
          </ul>
        </div>

        <div className="nav-child3">
          <i className="fa-regular fa-circle-user"></i>
        </div>
      </div>
    </div>
  );
};

export default Header;
