import axios from "axios";
import { useState, useEffect, useContext } from "react";
import { MyContext } from "../context/MyContext";

const Reserve = (props) => {
  const [className, setClassName] = useState("");
  const [isCancelClicked, setIsCancelClicked] = useState(false);
  const { isButtonClicked, handleButtonClick, refresh, handleRefresh } =
    useContext(MyContext);
 
  const getData = async () => {
    try {
      const response = await axios.get('http://localhost:3001/server');

      const selectedCarName = response.data.vehicles[response.data.vehicles.length - 1].car;
      const cars = response.data.cars[0];

      const matchedCardKey = Object.keys(cars).find(key => cars[key] === selectedCarName);

      setClassName(matchedCardKey);
      console.log(selectedCarName);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getData();

    // const interval = setInterval(getData, 1000); // Fetch data every 5 seconds

    // return () => clearInterval(interval);
  }, [refresh]);

// const cancelClickHandler = ()=>{
//   setIsCancelClicked(true);
// }

// setTimeout(() => {
  
// }, 0.01);
  
  return (
    <div style={{display: props.context ? "flex" : "none"}} className="reserve-cont">
      <div className="reserve">
        <div className="reserve-child1">
          <h1>COMPLETE RESERVATION</h1>
        </div>
        <div className="reserve-child2">
          <i class="fa-solid fa-circle-info"></i>
          <p>Upon completing this reservation, you will receive: </p>
          <p>
            A confirmation email with detailed information about your selected
            car model and next steps to finalize your purchase.
          </p>
        </div>
        <div className="reserve-child3">
          <div className="res-info-cont">
            <p>Vehicle Information</p>
            <div>
              <img className="category" src="category.png" alt="" />
              <p>Vehicle Category</p>
            </div>
            <div>
              <i className="fa-solid fa-car-side"></i>
              <p>Type Of Car</p>
            </div>
            <div>
              <i className="fa-solid fa-location-dot"></i>
              <p>Pick Up Location</p>
            </div>
            <div>
              <i className="fa-solid fa-location-dot"></i>
              <p>Drop Off Location</p>
            </div>
            <div>
              <i className="fa-solid fa-calendar-days"></i>
              <p>Pick Up Date</p>
            </div>
            <div>
              <i className="fa-solid fa-calendar-days"></i>
              <p>Drop Off Date</p>
            </div>
          </div>
          <div className="res-car">
            <p>CAR:</p>
            <img src={`${className}.png`} alt="" />
          </div>
        </div>
        <div className="reserve-child4">
          <button className="reserve-btn">Cancel</button>
          <button className="reserve-btn">Complete Reservation</button>
        </div> 
      </div>
    </div>
  );
};

export default Reserve;
