const FrontPage = () => {
  let scrollAmount = 2700;

  if (
    window.matchMedia("(min-width: 1019px) and (max-width: 1280px)").matches
  ) {
    scrollAmount = 4600;
  }
  if (window.matchMedia("(max-width: 1018px)").matches) {
    scrollAmount = 8400;
  }

  const handleButtonClick = () => {
    // Scroll down by the specified amount
    window.scrollTo({
      top: window.scrollY + scrollAmount,
    });
  };
  return (
    <div className="front">
      <img className="back" src="back_n.png" alt="" />
      <div className="front-child1">
        <h1>
          If comparable, It is no longer
          <span className="nova"> NOVA!</span>
        </h1>
        <button onClick={handleButtonClick} className="order">
          <p>Order now</p>{" "}
        </button>
        <button className="view">
          <p>View cars</p>
        </button>
      </div>
      <div className="front-child2">
        <img className="car car1" src="rc1.jpg" alt="" />
        <img className="car car2" src="rc2.jpg" alt="" />
      </div>
    </div>
  );
};

export default FrontPage;
