import { useState } from "react";
import Reserve from "./Reserve";
import axios from "axios";

const Book = () => {
  const [cars, setCars] = useState([]);
  const [formData, setFormData] = useState({
    category: "",
    car: "",
    pickupLoc: "",
    dropoffLoc: "",
    dateOfPickup: "",
    dateOfDropoff: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleCategoryChange = (event) => {
    const selectedCategory = event.target.value;
    setFormData({
      ...formData,
      category: selectedCategory,
    });
    switch (selectedCategory) {
      case "Car":
        setCars([
          "Jaguar F-TYPE",
          "Ferrari F430",
          "Tesla Model S",
          "Hyundai Elite",
          "Tesla Model S75",
          "Tesla Model X",
          "Tesla Model P90",
        ]);
        break;
      case "SUV/MUV":
        setCars([
          "Mahindra Scorpio",
          "Jeep Wrangler",
          "Mercedes-Benz",
          "Mahindra Thar",
          "Honda Elevate",
          "Tata Harrier",
          "Maruti Suzuki Grand",
        ]);
        break;
      case "Track":
        setCars([
          "Toyota Tacoma",
          "Ford Maverick XLT",
          "RAM 1500 Rebel",
          "Ford F-150 XLT",
          "Nissan Frontier SV",
          "Toyota Tundra Hybrid",
          "Hyundai Santa Cruz",
        ]);
        break;
      case "Van":
        setCars([
          "Mercedes-Benz",
          "Toyota Sienna",
          "Honda Odyssey",
          "Kia Carnival",
          "Chrysler Pacifica",
          "Ford Transit",
        ]);
        break;
      default:
        setCars([]);
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:3001/server", formData);
      console.log(`Your data has been submitted successfully!`, formData);
    } catch (err) {
      console.log("the error is" + err);
    }
  };

  return (
    <div className="book">
      <div className="book-topic-cont">
        <h1>Book a car</h1>
      </div>
      <form action="../Server/server" method="post" onSubmit={handleSubmit}>
        <div className="select-cont1">
          <div className="topic">
            <img src="category.png" alt="" />
            <p>
              Select Vehicle Category<b>*</b>
            </p>
          </div>
          <select
            name="category"
            value={formData.category}
            className="box"
            onChange={handleCategoryChange}
          >
            <option>Select Vehicle Category</option>
            <option value="Car">Car</option>
            <option value="SUV/MUV">SUV/MUV</option>
            <option value="Track">Track</option>
            <option value="Van">Van</option>
          </select>
        </div>
        <div className="select-cont2">
          <div className="topic">
            <i className="fa-solid fa-car-side"></i>
            <p>
              Select Your Car<b>*</b>
            </p>
            <h6 className={cars.length === 0 ? "message" : "msg"}>
              Select vehicle category first!
            </h6>
          </div>
          <select
            name="car"
            value={formData.car}
            onChange={handleInputChange}
            className="box"
          >
            <option>Select Your Car</option>
            {cars.map((car, index) => {
              return (
                <option value={car} key={index}>
                  {car}
                </option>
              );
            })}
          </select>
        </div>
        <div className="select-cont3">
          <div className="topic">
            <i className="fa-solid fa-location-dot"></i>
            <p>
              Pick-up<b>*</b>
            </p>
          </div>
          <select
            name="pickupLoc"
            value={formData.pickupLoc}
            onChange={handleInputChange}
            className="box"
          >
            <option>Select pick up location</option>
            <option value="Los Angeles, California">
              Los Angeles, California
            </option>
            <option value="Miami, Florida">Miami, Florida</option>
            <option value="New York City">New York City</option>
            <option value="Austin, Texas">Austin, Texas</option>
            <option value="Chicago, Illinois">Chicago, Illinois</option>
          </select>
        </div>

        <div className="select-cont4">
          <div className="topic">
            <i className="fa-solid fa-location-dot"></i>
            <p>
              Drop-of<b>*</b>
            </p>
          </div>
          <select
            name="dropoffLoc"
            value={formData.dropoffLoc}
            onChange={handleInputChange}
            className="box"
          >
            <option>Select drop off location</option>
            <option value="Los Angeles, California">
              Los Angeles, California
            </option>
            <option value="Miami, Florida">Miami, Florida</option>
            <option value="New York City">New York City</option>
            <option value="Austin, Texas">Austin, Texas</option>
            <option value="Chicago, Illinois">Chicago, Illinois</option>
          </select>
        </div>
        <div className="select-cont5">
          <div className="topic">
            <i className="fa-solid fa-calendar-days"></i>
            <p>
              Pick-up<b>*</b>
            </p>
          </div>
          <input
            type="date"
            name="dateOfPickup"
            value={formData.dateOfPickup}
            onChange={handleInputChange}
            className="date box"
          />
        </div>
        <div className="select-cont6">
          <div className="topic">
            <i className="fa-solid fa-calendar-days"></i>
            <p>
              Drop-of<b>*</b>
            </p>
          </div>
          <input
            type="date"
            name="dateOfDropoff"
            value={formData.dateOfDropoff}
            onChange={handleInputChange}
            className="date box"
          />
        </div>
        <button type="submit" className="search">
          <p>Search</p>
        </button>
      </form>
      <Reserve />
    </div>
  );
};

export default Book;
