import { useState, useEffect } from "react";

const FrontPage = () => {
  const [scrollAmount, setScrollAmount] = useState(2700);

  useEffect(() => {
    const handleResize = () => {
      if (
        window.matchMedia("(min-width: 1019px) and (max-width: 1280px)").matches
      ) {
        setScrollAmount(4600);
      } else if (
        window.matchMedia("(min-width: 471px) and (max-width: 1018px)").matches
      ) {
        setScrollAmount(8300);
      }  else if (window.matchMedia("(max-width: 470px)").matches) {
        setScrollAmount(7300);
      } else {
        setScrollAmount(2700); // Default value for other screen sizes
      }
    };

    handleResize(); // Call it once to set the initial scrollAmount

    window.addEventListener("resize", handleResize);

    // Cleanup the event listener when the component is unmounted
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const handleButtonClick = () => {
    // Scroll down by the specified amount
    window.scrollTo({
      top: window.scrollY + scrollAmount,
    });
  };

  return (
    <div className="front">
      <img className="back" src="back_n.png" alt="" />
      <div className="front-child1">
        <h1>
          If comparable, It is no longer
          <span className="nova"> NOVA!</span>
        </h1>
        <button onClick={handleButtonClick} className="order">
          <p>Order now</p>{" "}
        </button>
        <button className="view">
          <p>View cars</p>
        </button>
      </div>
      <div className="front-child2">
        <img className="car car1" src="rc1.jpg" alt="" />
        <img className="car car2" src="rc2.jpg" alt="" />
      </div>
    </div>
  );
};

export default FrontPage;
